import React, { useState } from "react";
import Logo from "../assets/pastaLogo.png";
import "../styles/Navbar.css";
import ReorderOutlinedIcon from "@mui/icons-material/ReorderOutlined";
import { Link } from "react-scroll"

function Navbar() {
  const [openLinks, setOpenLinks] = useState(false);
  const toggleNavbar = () => {
    setOpenLinks(!openLinks);
  };
  return (
    <div className="navbar">
      <div className="leftSide" id={openLinks ? "open" : "close"}>
        <img src={Logo} alt="Logo" />
        <div className="hiddenLinks">
          <a href="/">Home</a>
          <a href="#menu">Menu</a>
        </div>
      </div>
      <div className="rightSide">
        <Link className="link" to="/" spy={true} smooth={true} offset={-50} duration={500}>Home</Link>
        <Link className="link" to="menu" spy={true} smooth={true} offset={0} duration={500}>Menu</Link>
        <Link className="link" to="about" spy={true} smooth={true} offset={370} duration={500}>About</Link>
        <Link className="link" to="contact" spy={true} smooth={true} offset={0} duration={500}>Contact</Link>
        <button onClick={toggleNavbar}>
          <ReorderOutlinedIcon />
        </button>
      </div>
    </div>
  );
}

export default Navbar;
